import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/send-email', formData);
      // Handle success (e.g., show a success message)
      console.log('Email sent successfully');
    } catch (error) {
      // Handle error (e.g., show an error message)
      console.error('Error sending email:', error);
    }
  };

  return (
    <div>
    <form onSubmit={handleSubmit} className="max-w-md mx-auto mt-10 p-6 bg-gray-100 rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold mb-6 text-center">Contact Us</h2>
      <div className="mb-4">
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Your Name"
          className="w-full p-2 border rounded-md"
          required
        />
      </div>
      <div className="mb-4">
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Your Email"
          className="w-full p-2 border rounded-md"
          required
        />
      </div>
      <div className="mb-4">
        <textarea
          name="message"
          value={formData.message}
          onChange={handleChange}
          placeholder="Your Message"
          className="w-full p-2 border rounded-md resize-none"
          rows="5"
          required
        ></textarea>
      </div>
      <button type="submit" className="bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600">
        Send Message
      </button>
    </form>

    <div className="bg-white rounded-lg shadow-md p-6">
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Contact Information</h3>
      <p className="text-gray-600 mb-2">
        <span className="font-semibold">Address:</span> 123 Yoga Street, City, Country
      </p>
      <p className="text-gray-600 mb-2">
        <span className="font-semibold">Email:</span> info@yogateacher.com
      </p>
      <p className="text-gray-600 mb-2">
        <span className="font-semibold">Phone:</span> +1 234 567 890
      </p>
     </div>
     </div>

  );
};

export default ContactForm;
