import React, {useState, useEffect} from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import axios from 'axios';
import SimpleCalendar from './SimpleCalendar';


const AdminDashboard = () => {
  const localizer = momentLocalizer(moment);

  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get('/api/bookings');
        const data = response.data;
        console.log('Fetched bookings data:', data);
        setBookings(data);
      } catch (error) {
        console.error('Error fetching bookings:', error.message);
      }
    };

    fetchBookings();
  }, []);

  const events = bookings.map((booking) => {
    return {
      title: booking.email,
      start: moment.utc(booking.date)._d,
    };
  });
  console.log('All Events:', events);

  const TestCalendar = () => {
    const events = [
      { title: 'Event 1', start: new Date('2024-01-01T10:00:00Z') },
      { title: 'Event 2', start: new Date('2024-01-02T14:00:00Z') },
      // Add more test events as needed
    ];

    return (
      <div>
        <h2>Test Calendar</h2>
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          style={{ height: 400 }}
        />
      </div>
    );
  };


return (
  <div className="container mx-auto p-8">
    <h1 className="text-4xl font-bold mb-6">Admin Dashboard</h1>

    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Bookings Calendar</h2>
      {/* <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start" // Use the correct accessor
        style={{ height: 600 }} // Set the height of the calendar
      /> */}
      <SimpleCalendar events={bookings} />
    </div>
  </div>
);
};

export default AdminDashboard;
