      <h2 className="text-2xl font-semibold mb-6 text-center">Contact Us</h2>
      {/* Form Inputs */}
      <div className="mb-4">
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Your Name"
          className="w-full p-2 border rounded-md"
          required
        />
      </div>
      {/* ... Other form fields */}
      <div className="mb-4">
           <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Your Email"
            className="w-full p-2 border rounded-md"
