import React, {useState, useEffect} from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';

const AdminDashboard = () => {
  const localizer = momentLocalizer(moment);

  const [events, setEvents] = useState([]);

useEffect(() => {
  // Fetch booking data from the backend and update the state
  const fetchBookingData = async () => {
    try {
      const response = await fetch('/api/bookings');

      if (!response.ok) {
        throw new Error(`Error fetching booking data: ${response.statusText}`);
      }

      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error(error.message);
    }
  };

  fetchBookingData();
}, []);

return (
  <div className="container mx-auto p-8">
    <h1 className="text-4xl font-bold mb-6">Admin Dashboard</h1>

    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Bookings Calendar</h2>
      <Calendar
        localizer={localizer}
        events={bookings}
        startAccessor="start" // Replace with your start date property
        endAccessor="end" // Replace with your end date property
        style={{ height: 500 }} // Set the height of the calendar
      />
    </div>
  </div>
);
};

export default AdminDashboard;
